for (int  i = 0; i < 4; i++)
    {
        /* code */
       // cout<<i<<endl;
        if(i==2)  // if we want to stop the loop after the value is 2 then we can use this also we can use any other value

        {
           break; // this will break the loop and stop the execution of loop after the value of i is printed note that the value given in brackets will also come as output
        }         //will also come as output if the i is printed before the break statement
           cout<<i<<endl;   // if the cout statement is printed after the break then the 2 will not be included i.e the value of (i)
    }
    